import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { ToastrService } from 'ngx-toastr';

import { BasketModel } from 'src/app/models/basket';
import { ProductModel } from 'src/app/models/product';
import { __values } from 'tslib';

@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.scss']
})
export class ProductComponent implements OnInit {

  products: ProductModel[] = [{
    name: "süt", price: 17.50, imageUrl: "data:image/jpeg;base64,"
  },
  { name: "hurma şekeri", price: 27.50, imageUrl: "data:image/jpeg;base64," },
  { name: "pide", price: 65, imageUrl: "data:image/jpeg;base64," },
  { name: "baklava", price: 45.50, imageUrl: "data:image/jpeg;base64," }
  ];
  baskets: BasketModel[] = [];
  total: number = 0;
  @Output() myEvent: EventEmitter<any> = new EventEmitter();
  constructor(
   private toastr:ToastrService
  ) {
  }
  ngOnInit(): void { }
  addBasket(product: ProductModel) {
    let basketModel = new BasketModel;
    basketModel.product = product;
    console.log(document.getElementById("quantity -" + product.name))
    basketModel.quantity = parseInt((<HTMLInputElement>document.getElementById("quantity-" + product.name)).value);
    (<HTMLInputElement>document.getElementById("quantity-" + product.name)).value = "1";
    this.baskets.push(basketModel);
   // this.total = this.total + (basketModel.quantity * product.price);
   this.total=0;
    //console.log(this.baskets);
    this.myEvent.emit({ data: this.baskets });
   this.toastr.success(product.name + " " + "eklendi");
  }
}